ored\r\n      PGDATA: /var/lib/postgresql/data/myitsdata\r\n# mount posgresql volume on your own computer to persist the data\r\n    volumes:\r\n        - /var/lib/postgresql/data/myitsdata:/var/lib/postgresql/data/myitsdata:rw\r\n\r\n# This is an interface to admininstrate db in your docker envirnment on port 8080\r\n# To access it, open a browser on the url http://localhost:8080\r\n# This is only t help developers but will not be used in MyITS project\r\n  adminer:\r\n    image: adminer\r\n    restart: always\r\n    ports:\r\n      - 8080:8080\";s:11:\"description\";s:46:\"docker-compose to launch a PostgreSQL database\";s:4:\"vars\";s:0:\"\";s:9:\"shortcode\";s:1:\"0\";s:3:\"php\";s:1:\"0\";s:11:\"wptexturize\";s:1:\"0\";}}','yes'),(91055,'_transient_timeout_jetpack_jitm_77656dd4246a17a9067aed2fb708cd2','1530965006','no'),(91056,'_transient_jetpack_jitm_77656dd4246a17a9067aed2fb708cd2','a:1:{s:18:\"last_response_time\";i:1530964706;}','no'),(91061,'_transient_timeout_jetpack_jitm_ebc90351c668e23da9085f41bddae95','1530965008','no'),(91062,'_transient_jetpack_jitm_ebc90351c668e23da9085f41bddae95','a:1:{s:18:\"last_response_time\";i:1530964708;}','no'),(91063,'_transient_timeout_jetpack_jitm_964a05187f740d45355d60c713a3259','1530965011','no'),(91064,'_transient_jetpack_jitm_964a05187f740d45355d60c713a3259','a:1:{s:18:\"last_response_time\";i:1530964711;}','no'),(91120,'_transient_timeout_jetpack_jitm_9b199ae84b635441939128f91f334a5','1530965504','no'),(91121,'_transient_jetpack_jitm_9b199ae84b635441939128f91f334a5','a:1:{s:18:\"last_response_time\";i:1530965204;}','no'),(91133,'crayon_options','a:95:{s:5:\"theme\";s:7:\"classic\";s:7:\"preview\";b:1;s:15:\"enqueque-themes\";b:1;s:4:\"font\";s:6:\"monaco\";s:16:\"font-size-enable\";b:1;s:9:\"font-size\";i:12;s:11:\"line-height\";i:15;s:14:\"enqueque-fonts\";b:1;s:11:\"height-mode\";i:0;s:6:\"height\";s:3:\"500\";s:11:\"height-unit\";i:0;s:10:\"width-mode\";i:0;s:5:\"width\";s:3:\"500\";s:10:\"width-unit\";i:0;s:7:\"t